<?phprequire_once("includes.php");PushConnection::$BOTAPI_LOGIN='email';PushConnection::$BOTAPI_PASSWORD='hasło';$p = new PushConnection($_GET['to']);$m = new MessageBuilder();$time = time();$date = date("Y-m-d H:i:s");$from = $_GET['from'];if($p->isBot($from))	die("Botom mówimy NIE!");if($from != 34655403){	$msg = str_replace(array("'", '"', '`'), "", trim($HTTP_RAW_POST_DATA));}else{	$msg = $HTTP_RAW_POST_DATA;}$wyraz = explode(" ", $msg);$cmd = strtolower(str_replace(array(".", "/"), "", $wyraz[0]));$staff_cmd = substr($msg, 0, 2);$staff_cmd_cor = str_replace("::", "", $cmd);$user_db = $db->query("SELECT * FROM `users` WHERE `nr` = {$from}");if($user_db->num_rows == 0){	$new_nick = "GG_".substr($from, 0, strlen($from)-3)."***".rand(100,999);	$db->query("INSERT INTO `users` (`nr`, `nick`, `register_time`, `online`) VALUES ({$from}, '{$new_nick}', {$time}, 1)");	$m->addBBCode("[b]{$new_nick}[/b] właśnie zarejestrował się w Czaciek! Powitajmy go!")->setRecipients(get_users());	$p->push($m);	$m->clear();	$m->addBBCode("Zarejestrowałeś się! Wpisz [b]/pomoc[/b] aby zobaczyć stronę pomocy ;-)[br][br]Aby wyłączyć subskrypcję wpisz WYPISZ");	$m->reply();	$m->clear();}else{$user = $user_db->fetch_assoc();switch($user['staff']){	case "10":		$znak = "[color=ff0000][***][/color]";	break;	case "9":		$znak = "[color=ff0000][**][/color]";	break;	case "8":		$znak = "[color=ff0000][*$][/color]";	break;	case "7":		$znak = "[color=ff0000][$][/color]";	break;	case "6":		$znak = "[color=ff0000][*#][/color]";	break;	case "5":		$znak = "[color=ff0000][#][/color]";	break;	case "4":		$znak = "[color=ff0000][<#][/color]";	break;	case "3":		$znak = "[color=ff0000][*!][/color]";	break;	case "2":		$znak = "[color=ff0000][!][/color]";	break;	case "1":		$znak = "[color=ff0000][<!][/color]";	break;	default:		$znak = "";	break;}if($user['banned'] == 1)	die("Zostałeś zbanowany na tym czacie z powodu: ".$user['ban_reason']."	Możesz odwołać się pisząc do gg 34655403");if($user['online'] == 0){	if($cmd != "join" AND $cmd != "j"){		$m->addBBCode("Musisz wpisać [b]/join[/b] aby się zalogować! ;-)");		$m->reply();		$m->clear();		die();	}}if($msg{0} != "/" and $msg{0} != "." and $staff_cmd != "::" and $user['online'] == 1){	for($i=0;$i<count($wyraz);$i++){		if(substr($wyraz[$i], 0, 3) == "www"){			$link = base64_encode($wyraz[$i]);			$wyraz[$i] = "http://mrgod.pl/redirect.php?link=".$link;		}elseif(substr($wyraz[$i], 0, 4) == "http"){			$link = base64_encode($wyraz[$i]);			$wyraz[$i] = "http://mrgod.pl/redirect.php?link=".$link;		}	}	$msg = implode(" ", $wyraz);	$identyfikator = '';	if(isset($_GET['images']))		$identyfikator = $_GET['images'];	$add_link = '';	if($identyfikator != ''){		$obrazek = $p->getImage($identyfikator);		file_put_contents("../../mrgod/images/image{$identyfikator}.png", $obrazek);		$add_link = "[br]Załączony został obrazek, aby zobaczyć kliknij w link: http://mrgod.pl/show_image.php?img={$identyfikator}";	}	if($user['echo'] == 1){		$m->addBBCode("[b]{$znak}{$user['nick']}[/b]: {$msg}".$add_link)->setRecipients(get_users());		$p->push($m);		$m->clear();	}else{		$m->addBBCode("[b]{$znak}{$user['nick']}[/b]: {$msg}".$add_link)->setRecipients(get_users('echo', $from));		$p->push($m);		$m->clear();	}	$db->query("INSERT INTO `logs` (`who`, `content`, `time`, `type`) VALUES ('{$user['nick']}', '{$msg}', '{$time}', 1)");}elseif($staff_cmd == "::" and $user['staff'] != 0){	include("do_staff_command.php");	$db->query("INSERT INTO `logs` (`who`, `content`, `time`, `type`) VALUES ('{$user['nick']}', '{$msg}', '{$time}', 2)");}else{	include("do_command.php");	$db->query("INSERT INTO `logs` (`who`, `content`, `time`, `type`) VALUES ('{$user['nick']}', '{$msg}', '{$time}', 3)");}}?>